pages/checkout:
{{#partial "head"}}

{{{ checkout.checkout_head }}}
{{{ stylesheet '/assets/css/optimized-checkout.css' }}}
{{ getFontsCollection }}

<script type="text/javascript">
    window.language = {{{langJson 'optimized_checkout'}}};
</script>

<style>

#countryCodeInput option{

    display: none;

  }


  
  #countryCodeInput option[label="Hong Kong"],
  #countryCodeInput option[label="Singapore"],
  #countryCodeInput option[label="Australia"],
  #countryCodeInput option[label="New Zealand"],
  
  
  #countryCodeInput option[label="Canada"],
  #countryCodeInput option[label="United States"],
  #countryCodeInput option[label="United Kingdom"],
  
  #countryCodeInput option[label="Belgium"],
  #countryCodeInput option[label="Brunei Darussalam"],
  #countryCodeInput option[label="Cambodia"],
  #countryCodeInput option[label="Czech Republic"],
  #countryCodeInput option[label="France"],
  #countryCodeInput option[label="Germany"],
  #countryCodeInput option[label="Indonesia"],
  #countryCodeInput option[label="Japan"],
  #countryCodeInput option[label="Luxembourg"],
  #countryCodeInput option[label="Malaysia"],
  #countryCodeInput option[label="Myanmar"],
  #countryCodeInput option[label="Netherlands"],
  #countryCodeInput option[label="Pakistan"],
  #countryCodeInput option[label="Philippines"],
  #countryCodeInput option[label="Poland"],
  #countryCodeInput option[label="Portugal"],
  #countryCodeInput option[label="Romania"],
  #countryCodeInput option[label="Korea, Republic of"],
  #countryCodeInput option[label="Spain"],
  #countryCodeInput option[label="Sri Lanka"],
  #countryCodeInput option[label="Sweden"],
  #countryCodeInput option[label="Thailand"],
  #countryCodeInput option[label="Vietnam"]{

    display: initial;

  }

</style>

{{/partial}}

{{#partial "page"}}
<header class="checkoutHeader optimizedCheckout-header">
    <div class="checkoutHeader-content">
        <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
        <h2 class="checkoutHeader-heading">
            <a class="checkoutHeader-link" href="{{urls.home}}">
                {{#if checkout.header_image}}
                    <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                {{ else }}
                    <span class="header-logo-text">{{settings.store_logo.title}}</span>
                {{/if}}
            </a>
        </h2>
    </div>
</header>

{{{ checkout.checkout_content }}}

{{{ footer.scripts }}}

{{/partial}}

{{> layout/empty}}
